function fadeOut(el){el.style.opacity=1;(function fade(){if((el.style.opacity-=0.1)<0){el.style.display="none"}else{requestAnimationFrame(fade)}})()}function fadeIn(el,display){el.style.opacity=0;el.style.display=display||"block";(function fade(){var val=parseFloat(el.style.opacity);if(!((val+=0.1)>1)){el.style.opacity=val;requestAnimationFrame(fade)}})()}setTimeout(function(){fadeOut(document.getElementsByClassName("page-loader")[0])},800);
function openBar() {
    let node = document.getElementsByClassName('site-nav')[0];
    if (node.classList.length == 1) {
        node.classList.add('site-nav-on');
        node.style.display = 'block';
    } else {
        node.classList.remove('site-nav-on');
        node.style.display = '';
    }
}
